/* eslint-disable import/prefer-default-export */
import { CATEGORIES_ACTION_TYPES } from './categories.types';

const INITIAL_STATE = {
  categoriesMap: {},
};

export const categoriesReduser = (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case CATEGORIES_ACTION_TYPES.SET_CATEGORIES_MAP:
      return {
        ...state,
        categoriesMap: payload,
      };
    default:
      throw new Error(`Unhandled type ${type} in categoriesReduser`);
  }
};
