import styled from 'styled-components';

export const CheckoutItemContainer = styled.div`
  width: 100%;
  display: flex;
  min-height: 100px;
  border-bottom: 1px solid darkgrey;
  padding: 15px 0;
  font-size: 20px;
  align-items: center;
`;

export const Arrow = styles.div`
  cursor: pointer;
`;

export const Value = styles.div`
  margin: 0 10px;
`;

export const Quantiy = styles.div`
  display: flex;
`;

export const ImgContainer = styled.div`
  width: 23%;
    padding-right: 15px;

    img {
      width: 100%;
      height: 100%;
    }

  span {
    width: 23%;
  }
`;

