/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import { useSelector } from 'react-redux';
import { CheckoutContainer, CheckoutHeader, CheckoutHeaderBlock, Total } from './checkout.styles';
import { selectCardItems, selectCardTotal } from '../../store/card/card.selector';
import CheckoutItem from '../../components/checkout-item/checkout-item.component';

const Checkout = () => {
  const cardItems = useSelector(selectCardItems);
  const totalPrice = useSelector(selectCardTotal);
  return (
    <CheckoutContainer>
      <CheckoutHeader>
        <Ch>
          <span>Product</span>
        </Ch>
        <Ch>
          <span>Description</span>
        </Ch>
        <Ch>
          <span>Quantity</span>
        </Ch>
        <Ch>
          <span>Price</span>
        </Ch>
        <Ch>
          <span>Remove</span>
        </Ch>
      </CheckoutHeader>
      {
      cardItems.map((cardItem) => (
        <CheckoutItem key={cardItem.id} cardItem={cardItem} />
      ))
    }
      <Total>
        Total: $
        {totalPrice}
      </Total>
    </CheckoutContainer>
  );
};

export default Checkout;
