import {
  takeLatest, all, call, put,
} from 'redux-saga/effects';
import { SignInSuccess, SignInFailed } from './user.action';
import { USER_ACTION_TYPES } from './user.types';
import { getCurrentUseer, createUserDocumentFromAuth } from '../../utils/firebase/firebase.utils';

export function* getSnapshotFromUserAuth(userAuth, addisionalDetails) {
  try {
    const userShapshot = yield call(createUserDocumentFromAuth, userAuth, addisionalDetails);
    yield put(signInSuccess({id: userShapshot.id, ...userSnapshot.data()}))
  } catch (error) {
    yield put(SignInFailed);
  }
}

export function* isUserAuthenticated() {
  try {
    const userAuth = yield call(getCurrentUseer);
    if (!userAuth) return;
    yield call(getSnapshotFromUserAuth, userAuth);
  } catch (error) {
    yield put(SignInFailed);
  }
}

export function* onCheckUserSession() {
  yield takeLatest(USER_ACTION_TYPES.CHECK_USER_SESSION, isUserAuthenticated);
}

export function* userSaga() {
  yield all([call(onCheckUserSession)]);
}
