/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import './checkout.styles.scss';
import { useContext } from 'react';
import { CardContext } from '../../context/card.context';

const Checkout = () => {
  const { cardItems, addItemToCard, removeItemFromCard } = useContext(CardContext);
  return (
    <div className="checkout-container">
      <div className="checkout-header">
        <div className="header-block">
          <span>Product</span>
        </div>
        <div className="header-block">
          <span>Description</span>
        </div>
        <div className="header-block">
          <span>Quantity</span>
        </div>
        <div className="header-block">
          <span>Price</span>
        </div>
        <div className="header-block">
          <span>Remove</span>
        </div>
      </div>
      <h1>Checkout page</h1>
      {
      cardItems.map((cardItem) => {
        const { id, name, quantity } = cardItem;
        return (
          <div key={id}>
            <h2>{name}</h2>
            <span>{quantity}</span>
            <br />
            <span onClick={() => removeItemFromCard(cardItem)}>decrement</span>
            <br />
            <span onClick={() => addItemToCard(cardItem)}>increment</span>
          </div>
        );
      })
    }
      <span className="Total">Total: 0</span>
    </div>
  );
};

export default Checkout;
