/* eslint-disable import/prefer-default-export */
import { CATEGORIES_ACTION_TYPES } from './categories.types';

const INITIAL_STATE = {
  categories: [],
};

export const categoriesReduser = (state = INITIAL_STATE, { type, payload }) => {
  switch (type) {
    case CATEGORIES_ACTION_TYPES.SET_CATEGORIES:
      return {
        ...state,
        categories: payload,
      };
    default:
      return state;
  }
};
