import styled from 'styled-components';

export const CategoryPreviewCOntainer = styled.div`
  display: flex;
  flex-direction: column;
  margin-bottom: 30px;

  h2 {
    font-size: 28px;
    margin-bottom: 25px;
    cursor: pointer;
  }

  div {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    column-gap: 20px;
  }
`;
