/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
/* eslint-disable react/prop-types */
import { useSelector, useDispatch } from 'react-redux';
import './checkout-item.styles.scss';
import { addItemToCard, removeItemFromCard, clearItemFromCard } from '../../store/card/card.action';
import { selectCardItems } from '../../store/card/card.selector';

const CheckoutItem = ({ cardItem }) => {
  const dispatch = useDispatch();
  const cardItems = useSelector(selectCardItems);

  const {
    name, quantity, price, imageUrl,
  } = cardItem;
  return (
    <div className="checkout-item-container">
      <div className="image-container">
        <img src={imageUrl} alt={name} />
      </div>
      <span className="name">{name}</span>
      <span className="quantity">
        <div className="arrow" onClick={() => dispatch(removeItemFromCard(cardItems, cardItem))}>&#10094;</div>
        <div className="value">{quantity}</div>
        <div className="arrow" onClick={() => dispatch(addItemToCard(cardItems, cardItem))}>&#10095;</div>
      </span>
      <span className="name">{price}</span>
      <div onClick={() => dispatch(clearItemFromCard(cardItems, cardItem))} className="remove-button">&#10005;</div>
    </div>
  );
};

export default CheckoutItem;
