/* eslint-disable jsx-a11y/no-static-element-interactions */
/* eslint-disable jsx-a11y/click-events-have-key-events */
import './checkout.styles.scss';
import { useContext } from 'react';
import { CardContext } from '../../context/card.context';

const Checkout = () => {
  const { cardItems, addItemToCard } = useContext(CardContext);
  return (
    <div>
      <h1>Checkout page</h1>
      <div>
        {
          cardItems.map((cardItem) => {
            const { id, name, quantity } = cardItem;
            return (
              <div key={id}>
                <h2>{name}</h2>
                <span>{quantity}</span>
                <span onClick={() => addItemToCard(cardItem)}>decrement</span>
                <span non-interactive elements with click handlers must have at least one keyboard listener  >increment</span>
              </div>
            );
          })
        }
      </div>
    </div>
  );
};

export default Checkout;
