/* eslint-disable jsx-a11y/label-has-associated-control */
import { useState } from 'react';
impo

const defaultFormFields = {
  displayName: '',
  email: '',
  password: '',
  confirmPassword: '',
};

const SignUpForm = () => {
  const [formFields, setFormFields] = useState(defaultFormFields);
  const {
    displayName,
    email,
    password,
    confirmPassword,
  } = formFields;

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormFields({ ...formFields, [name]: value });
  };

  const sign = 'Sign Up';
  return (
    <div>
      <h1>{sign}</h1>
      <form onSubmit={() => {}}>
        <label>Display Name</label>
        <input required type="text" onChange={handleChange} name="displayName" vitus={displayName} />
        <label>Email</label>
        <input required type="email" onChange={handleChange} name="email" value={email} />
        <label>Password</label>
        <input required type="password" onChange={handleChange} name="password" value={password} />
        <label>Confirm Password</label>
        <input required type="password" onChange={handleChange} name="confirmPassword" value={confirmPassword} />
        <button type="submit">Sign Up</button>
      </form>
    </div>
  );
};

export default SignUpForm;
