/* eslint-disable arrow-body-style */
/* eslint-disable no-unused-vars */
/* eslint-disable react/prop-types */
import { createReducer } from '@reduxjs/toolkit';
import {
  createContext, useReducer,
} from 'react';

const addCardItem = (cardItems, productToAdd) => {
  const productExist = cardItems.find((item) => item.id === productToAdd.id);

  if (productExist) {
    return cardItems.map((item) => (
      item.id === productToAdd.id ? { ...item, quantity: item.quantity + 1 } : item));
  }

  return [...cardItems, { ...productToAdd, quantity: 1 }];
};

const removeCardItem = (cardItems, productToRemove) => {
  const productExist = cardItems.find((item) => item.id === productToRemove.id);

  if (productExist.quantity === 1) {
    return cardItems.filter((item) => item.id !== productToRemove.id);
  }

  return cardItems.map((item) => (
    item.id === productToRemove.id ? { ...item, quantity: item.quantity - 1 } : item));
};

const clearCardItem = (cardItems, productToClear) => {
  return cardItems.filter((item) => item.id !== productToClear.id);
};

export const CardContext = createContext({
  isCardOpen: false,
  setIsCardOpen: () => {},
  cardItems: [],
  addItemToCard: () => {},
  removeItemFromCard: () => {},
  clearItemFromCard: () => {},
  cardCount: 0,
  totalPrice: 0,
});

export const CARD_ACTION_TYPES = {
  SET_CART_ITEMS: 'SET_CART_ITEMS',
  SET_IS_CARD_OPEN: 'SET_IS_CARD_OPEN',
};

const INITIAL_STATE = {
  isCardOpen: false,
  cardItems: [],
  cardCount: 0,
  totalPrice: 0,
};

const cardReducer = (state, { type, payload }) => {
  switch (type) {
    case CARD_ACTION_TYPES.SET_CART_ITEMS:
      return {
        ...state,
        ...payload,
      };
    case CARD_ACTION_TYPES.SET_IS_CARD_OPEN:
      return {
        ...state,
        isCardOpen: payload,
      };
    default:
      throw new Error(`unhandled type ${type} in cardReducer`);
  }
};

export const CardProvider = ({ children }) => {
  const [state, dispatch] = useReducer(createReducer, INITIAL_STATE);
  const {
    cardItems, cardCount, totalPrice, isCardOpen,
  } = state;

  const updateCardItems = (newCardItems) => {
    const newCardCount = newCardItems.reduce((acc, item) => acc + item.quantity, 0);
    const newCardTotal = newCardItems.reduce((acc, item) => acc + item.quantity * item.price, 0);

    dispatch({
      type: SET_CART_ITEMS,
      payload: { cartItems: newCardItems, cardTotal: newCardTotal, cardCount: newCardCount },
    });
  };

  const addItemToCard = (productToAdd) => {
    const newCardItems = addCardItem(cardItems, productToAdd);
    updateCardItems(newCardItems);
  };

  const removeItemFromCard = (productToRemove) => {
    const newCardItems = removeCardItem(cardItems, productToRemove);
    updateCardItems(newCardItems);
  };

  const clearItemFromCard = (productToRemove) => {
    const newCardItems = clearCardItem(cardItems, productToRemove);
    updateCardItems(newCardItems);
  };

  const setIsCardOpen = (bool) => {
    dispatch({ type: SET_IS_CARD_OPEN, });
  };

  const value = {
    isCardOpen,
    setIsCardOpen: () => {},
    cardItems,
    addItemToCard,
    removeItemFromCard,
    cardCount,
    clearItemFromCard,
    totalPrice,
  };

  return (
    <CardContext.Provider value={value}>
      { children }
    </CardContext.Provider>
  );
};
